import React, { useRef } from 'react'; // *** ÁßªÈô§ useEffect ***
import { Form, Button, InputGroup, Spinner } from 'react-bootstrap';

function MessageInput({
    currentMessage,
    onMessageChange,
    onSendMessage,
    onGetFeedback,
    isLoading,
    isGettingFeedback,
    disabled
}) {
  const textareaRef = useRef(null); // Ref ‰ªçÁÑ∂‰øùÁïôÔºå‰ΩÜ‰∏çÁî®ÊñºÈ´òÂ∫¶Ë™øÊï¥

  // *** ÁßªÈô§Ëá™ÂãïË™øÊï¥È´òÂ∫¶ÁöÑ useEffect ***
  /*
  useEffect(() => {
    if (textareaRef.current) {
      textareaRef.current.style.height = 'auto'; // Reset height
      textareaRef.current.style.height = `${textareaRef.current.scrollHeight}px`; // Set to scroll height
    }
  }, [currentMessage]);
  */

  const handleKeyDown = (e) => {
    if (e.key === 'Enter' && !e.shiftKey && !isLoading && !isGettingFeedback && !disabled && currentMessage.trim()) {
      e.preventDefault();
      onSendMessage();
    }
  };

  const handleSendClick = () => {
      if (!isLoading && !isGettingFeedback && !disabled && currentMessage.trim()) {
          onSendMessage();
      }
  }

  const handleFeedbackClick = () => {
      if (!isLoading && !isGettingFeedback && !disabled) {
          onGetFeedback();
      }
  }

  return (
    <InputGroup>
      {/* Âø´ÈÄüÂõûÈ•ãÊåâÈàï */}
      <Button
        variant="outline-info"
        onClick={handleFeedbackClick}
        disabled={isLoading || isGettingFeedback || disabled}
        title="ËÆì AI ÈáùÂ∞çÊú¨Ê¨°Â∞çË©±Áµ¶Âá∫ÂõûÈ•ã"
      >
        {isGettingFeedback ? (
            <>
              <Spinner as="span" animation="border" size="sm" role="status" aria-hidden="true" />
              <span className="visually-hidden">Ë´ãÊ±Ç‰∏≠...</span>
            </>
        ) : (
            "ü§î ÊàëË™™ÂæóÂ¶Ç‰ΩïÔºü"
        )}
      </Button>

      <Form.Control
        ref={textareaRef} // Ref ‰ªçÁÑ∂ÂèØ‰ª•‰øùÁïôÁµ¶Êú™‰æÜÂèØËÉΩÁöÑÂÖ∂‰ªñÁî®ÈÄî
        as="textarea"
        rows={2} // *** Ë®≠ÂÆöÂõ∫ÂÆöÁöÑÂàùÂßãË°åÊï∏Ôºå‰æãÂ¶Ç 2 Êàñ 3 ***
        placeholder={disabled ? "Ë´ãÂÖàË®≠ÂÆöÁõÆÊ®ô‰∏¶ÈÅ∏ÊìáËßíËâ≤..." : "Ëº∏ÂÖ•Ë®äÊÅØ (Shift+Enter ÊèõË°å)..."}
        value={currentMessage}
        onChange={(e) => onMessageChange(e.target.value)}
        onKeyDown={handleKeyDown}
        disabled={isLoading || isGettingFeedback || disabled}
        // *** ‰øùÊåÅÈÄô‰∫õÊ®£ÂºèÔºåÂÖÅË®±ÂÖßÈÉ®ÊªæÂãï ***
        style={{ resize: 'none', overflowY: 'auto', maxHeight: '150px' }}
        aria-label="Ë®äÊÅØËº∏ÂÖ•Ê°Ü"
      />

      {/* Ë™ûÈü≥Ëº∏ÂÖ•È†êÁïôÂúñÁ§∫ (Êú™‰æÜÂäüËÉΩ) */}
      {/*
      <Button variant="outline-secondary" disabled={isLoading || isGettingFeedback || disabled} title="Ë™ûÈü≥Ëº∏ÂÖ• (Â∞öÊú™ÂïüÁî®)">
          <i className="bi bi-mic"></i>
      </Button>
      */}

      <Button
        variant="primary"
        onClick={handleSendClick}
        disabled={isLoading || isGettingFeedback || disabled || !currentMessage.trim()}
      >
        {isLoading ? (
            <>
                <Spinner as="span" animation="border" size="sm" role="status" aria-hidden="true"/>
                <span className="visually-hidden">ÂÇ≥ÈÄÅ‰∏≠...</span>
            </>
        ) : (
             <i className="bi bi-send"></i>
        )}
      </Button>
    </InputGroup>
  );
}

export default MessageInput;
