function TrainingRoom({ selectedCharacter }) {
  const [goal, setGoal] = useState('');
  const [messages, setMessages] = useState([]);
  const [currentMessage, setCurrentMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [isGettingFeedback, setIsGettingFeedback] = useState(false);
  const [error, setError] = useState(null);
  const currentGoalRef = useRef(goal);

  // Áî¢ÁîüÁî®ÊñºÂÑ≤Â≠òÁöÑ key
  const getStorageKey = useCallback(() => {
      if (!selectedCharacter || !goal.trim()) return null;
      const goalKey = goal.trim().substring(0, 50).replace(/[^a-zA-Z0-9-_]/g, '_'); // ÂÖÅË®±Â∫ïÁ∑öÂíåÈÄ£Â≠óËôü
      return `${CHAT_HISTORY_PREFIX}${selectedCharacter.id}_${goalKey}`;
  }, [selectedCharacter, goal]);

  // Âä†ËºâËÅäÂ§©Ë®òÈåÑ
  useEffect(() => {
    currentGoalRef.current = goal;
    const storageKey = getStorageKey();
    if (storageKey) {
      const savedHistory = localStorage.getItem(storageKey);
      if (savedHistory) {
        try {
          setMessages(JSON.parse(savedHistory));
        } catch (e) {
          console.error("Failed to parse chat history", e);
          setMessages([]);
        }
      } else {
        setMessages([]);
      }
    } else if (selectedCharacter) {
        setMessages([]);
    }
     setError(null);
  }, [selectedCharacter, goal, getStorageKey]);

   // ‰øùÂ≠òËÅäÂ§©Ë®òÈåÑ
   useEffect(() => {
     const storageKey = getStorageKey();
     if (storageKey && messages.length > 0) {
       localStorage.setItem(storageKey, JSON.stringify(messages));
     }
     else if (storageKey && messages.length === 0) {
         localStorage.removeItem(storageKey);
     }
   }, [messages, getStorageKey]);


  // Ê∏ÖÈô§Áï∂ÂâçÁõÆÊ®ôÁöÑËÅäÂ§©Ë®òÈåÑ
  const handleClearChat = () => {
      if (window.confirm(`Á¢∫ÂÆöË¶ÅÊ∏ÖÈô§Ëàá ${selectedCharacter?.name} ÈóúÊñºÁõÆÊ®ô„Äå${goal}„ÄçÁöÑÊâÄÊúâËÅäÂ§©Ë®òÈåÑÂóéÔºü`)) {
          setMessages([]);
          const storageKey = getStorageKey();
          if (storageKey) {
              localStorage.removeItem(storageKey);
          }
      }
  }

  // ÁôºÈÄÅË®äÊÅØËôïÁêÜ
  const handleSendMessage = async () => {
    if (!currentMessage.trim() || !selectedCharacter) return;
    if (!goal.trim()) {
      setError("Ë´ãÂÖàË®≠ÂÆöÊú¨Ê¨°ËÅäÂ§©ÁöÑÁõÆÊ®ôÔºÅ");
      return;
    }
    if (isLoading || isGettingFeedback) return;

    const newUserMessage = {
      id: Date.now(),
      sender: 'user',
      text: currentMessage,
    };

    const currentInput = currentMessage;
    // Á´ãÂç≥Êõ¥Êñ∞ UI
    setMessages(prevMessages => [...prevMessages, newUserMessage]);
    setCurrentMessage('');
    setIsLoading(true);
    setError(null);

    try {
      const historyForOllama = messages.map(msg => ({
        role: msg.sender === 'user' ? 'user' : 'assistant',
        content: msg.text
      }));
      // ‰ΩøÁî®ÊúÄÊñ∞ÁöÑ user message (currentInput) ÊßãÂª∫ÂÇ≥ÈÅûÁµ¶ API ÁöÑÊ≠∑Âè≤
      const requestHistory = [...historyForOllama, { role: 'user', content: currentInput }];


      const aiResponseText = await sendMessageToOllama(
        goal,
        requestHistory, // ‰ΩøÁî®ÂåÖÂê´ÊúÄÊñ∞Ê∂àÊÅØÁöÑÊ≠∑Âè≤
        selectedCharacter,
        "llama3.2:latest" // <--- Á¢∫Ë™ç‰ΩøÁî®Êñ∞Ê®°ÂûãÂêçÁ®±
      );

      const newAiMessage = {
        id: Date.now() + 1,
        sender: 'ai',
        text: aiResponseText,
      };
      // Ê≥®ÊÑèÔºöÈÄôË£°ÁöÑ setMessages ÊòØÂü∫Êñº„ÄåË´ãÊ±ÇÁôºËµ∑ÊôÇ„ÄçÁöÑ messages ÁãÄÊÖã‰æÜÊ∑ªÂä† AI ÂõûË¶Ü
      // ÁÇ∫‰∫ÜÁ¢∫‰øù user message ËÇØÂÆöÂú® AI message ‰πãÂâçÔºåÊàëÂÄëÁõ¥Êé•Ê∑ªÂä†Âú® newUserMessage ‰πãÂæå
      setMessages(prevMessages => [...prevMessages, newAiMessage]);

    } catch (err) {
      console.error("Error contacting Ollama:", err);
      const errorMsg = err.message || "Ëàá AI ÈÄöË®äÊôÇÁôºÁîüÈåØË™§„ÄÇ";
      setError(errorMsg);
      const errorAiMessage = {
        id: Date.now() + 1,
        sender: 'ai',
        text: `ü§ñ Êä±Ê≠âÔºåËôïÁêÜÊôÇÈÅáÂà∞ÂïèÈ°åÔºö${errorMsg}`,
      };
      // ÂêåÊ®£ÔºåÊ∑ªÂä†Âú®ÊúÄÊñ∞ÁöÑ user message ‰πãÂæå
       setMessages(prevMessages => [...prevMessages, errorAiMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  // --- TODO: ÂõûÈ•ãËß£ÊûêÂáΩÊï∏ ---
  // ÈÄôÂÄãÂáΩÊï∏ÈúÄË¶ÅÊ†πÊìö getFeedbackFromOllama ËøîÂõûÁöÑÂ≠ó‰∏≤Ê†ºÂºè‰æÜËß£Êûê
  const parseFeedback = (feedbackText) => {
      let summary = "ÁÑ°Ê≥ïËß£ÊûêÂõûÈ•ãÊëòË¶Å„ÄÇ";
      let scores = {
          clarity: null, empathy: null, confidence: null, appropriateness: null, goalAchievement: null
      };

      try {
          // Ëß£ÊûêÊëòË¶Å
          const summaryMatch = feedbackText.match(/\[Feedback Summary\]\s*([\s\S]*?)\s*\[Scores\]/);
          if (summaryMatch && summaryMatch[1]) {
              summary = summaryMatch[1].trim();
          } else {
             // ÂòóË©¶ÂÇôÁî®Ê®°ÂºèÔºåÂ¶ÇÊûúÊ≤íÊúâ [Scores] Ê®ôÁ±§
              const summaryOnlyMatch = feedbackText.match(/\[Feedback Summary\]\s*([\s\S]*)/);
              if (summaryOnlyMatch && summaryOnlyMatch[1]) {
                summary = summaryOnlyMatch[1].trim();
                console.warn("Feedback parsing: Only found summary, scores section missing or malformed.");
              } else {
                console.warn("Feedback parsing: Could not find [Feedback Summary] section.");
                // Â¶ÇÊûúÈÄ£ÊëòË¶ÅÈÉΩÊâæ‰∏çÂà∞ÔºåÂèØËÉΩÊï¥ÂÄãÂõûÈ•ãÈÉΩÊòØÈùûÁµêÊßãÂåñÁöÑ
                summary = feedbackText; // Â∞áÊï¥ÂÄãÊñáÊú¨Ë¶ñÁÇ∫ÊëòË¶Å
              }
          }

          // Ëß£ÊûêÂàÜÊï∏
          const scoresTextMatch = feedbackText.match(/\[Scores\]\s*([\s\S]*)/);
          if (scoresTextMatch && scoresTextMatch[1]) {
              const scoreLines = scoresTextMatch[1].trim().split('\n');
              scoreLines.forEach(line => {
                  const parts = line.split(':');
                  if (parts.length === 2) {
                      const key = parts[0].trim().toLowerCase().replace(/\s+/g, ''); // ËΩâÂ∞èÂØ´‰∏¶ÁßªÈô§Á©∫Ê†º
                      const value = parts[1].trim();
                      if (value.toLowerCase() === 'n/a') {
                          scores[key] = null; // N/A Ë¶ñÁÇ∫ null
                      } else {
                          const score = parseInt(value, 10);
                          if (!isNaN(score)) {
                              if (key === 'clarity') scores.clarity = Math.max(0, Math.min(100, score));
                              else if (key === 'empathy') scores.empathy = Math.max(0, Math.min(100, score));
                              else if (key === 'confidence') scores.confidence = Math.max(0, Math.min(100, score));
                              else if (key === 'appropriateness') scores.appropriateness = Math.max(0, Math.min(100, score));
                              else if (key === 'goalachievement') scores.goalAchievement = Math.max(0, Math.min(100, score));
                          }
                      }
                  }
              });
          } else if (!summaryOnlyMatch){ // Â¶ÇÊûúÈÄ£ [Scores] ÈÉΩÊ≤íÂåπÈÖçÂà∞Ôºå‰∏î‰∏çÊòØÂè™ÊúâÊëòË¶ÅÁöÑÊÉÖÊ≥Å
              console.warn("Feedback parsing: Could not find [Scores] section.");
          }
      } catch (e) {
          console.error("Error parsing feedback text:", e);
          // Âá∫ÈåØÊôÇËøîÂõûÂéüÂßãÊñáÊú¨‰ΩúÁÇ∫ÊëòË¶ÅÔºåÂàÜÊï∏ÁÇ∫Á©∫
          summary = feedbackText;
      }

      console.log("Parsed Feedback:", { summary, scores });
      return { summary, scores };
  }
  // --- ÁµêÊùü TODO ---


  // Áç≤ÂèñÂõûÈ•ãËôïÁêÜ
  const handleGetFeedback = async () => {
      if (!selectedCharacter || !goal.trim() || messages.filter(m => m.sender !== 'system').length === 0) { // Á¢∫‰øùÊúâÈùûÁ≥ªÁµ±Ê∂àÊÅØ
          alert("Ë´ãÂÖàË®≠ÂÆöÁõÆÊ®ô„ÄÅÈÅ∏ÊìáËßíËâ≤‰∏¶ÈÄ≤Ë°åËá≥Â∞ë‰∏ÄËº™Â∞çË©±ÔºåÊâçËÉΩË´ãÊ±ÇÂõûÈ•ã„ÄÇ");
          return;
      }
      if (isLoading || isGettingFeedback) return;

      setIsGettingFeedback(true);
      setError(null);
      const feedbackRequestMessage = {
          id: Date.now(),
          sender: 'system',
          text: '‚è≥ Ê≠£Âú®Âêë AI Ë´ãÊ±ÇÂ∞çË©±ÂõûÈ•ã...'
      };
      setMessages(prevMessages => [...prevMessages, feedbackRequestMessage]);


      try {
          const historyForFeedback = messages
            .filter(msg => msg.sender !== 'system') // ÈÅéÊøæÊéâÁ≥ªÁµ±Ê∂àÊÅØ
            .map(msg => ({
                role: msg.sender === 'user' ? 'user' : 'assistant',
                content: msg.text
            }));

          const feedbackRawText = await getFeedbackFromOllama(
              goal,
              historyForFeedback,
              selectedCharacter,
              "llama3.2:latest" // <--- Á¢∫Ë™ç‰ΩøÁî®Êñ∞Ê®°ÂûãÂêçÁ®±
          );

          // --- TODO: Ëß£Êûê‰∏¶ÂÑ≤Â≠òÂõûÈ•ã ---
          const { summary, scores } = parseFeedback(feedbackRawText);

          const feedbackMessage = {
              id: Date.now() + 1,
              sender: 'ai',
              // Âú®ËÅäÂ§©‰∏≠È°ØÁ§∫ÁµêÊßãÂåñÁöÑÂõûÈ•ãÔºàÊàñÂÉÖÈ°ØÁ§∫ÊëòË¶ÅÔºâ
              text: `üí° **Êú¨Ê¨°Â∞çË©±ÂõûÈ•ã (${goal})**\n\n**ÊëòË¶Å:**\n${summary}\n\n**Ë©ïÂàÜ (0-100):**\n- Ê∏ÖÊô∞Â∫¶: ${scores.clarity ?? 'N/A'}\n- ÂêåÁêÜÂøÉ: ${scores.empathy ?? 'N/A'}\n- Ëá™‰ø°: ${scores.confidence ?? 'N/A'}\n- ÈÅ©Áï∂ÊÄß: ${scores.appropriateness ?? 'N/A'}\n- ÁõÆÊ®ôÈÅîÊàê: ${scores.goalAchievement ?? 'N/A'}`
          };

          // ÂÑ≤Â≠òÂà∞ localStorage ‰æõ FeedbackWall ‰ΩøÁî®
          const newFeedbackEntry = {
              timestamp: Date.now(),
              goal: goal,
              characterId: selectedCharacter.id,
              characterName: selectedCharacter.name,
              scores: scores, // ÂÑ≤Â≠òËß£ÊûêÂæåÁöÑÂàÜÊï∏Â∞çË±°
              summary: summary, // ÂÑ≤Â≠òËß£ÊûêÂæåÁöÑÊëòË¶Å
              rawFeedback: feedbackRawText // (ÂèØÈÅ∏) ÂÑ≤Â≠òÂéüÂßãÊñáÊú¨‰æõË™øË©¶
          };
          const existingFeedback = JSON.parse(localStorage.getItem(FEEDBACK_STORAGE_KEY) || '[]');
          localStorage.setItem(FEEDBACK_STORAGE_KEY, JSON.stringify([...existingFeedback, newFeedbackEntry]));
          // --- ÁµêÊùü TODO ---


          // ÁßªÈô§"Ê≠£Âú®Ë´ãÊ±Ç"ÁöÑË®äÊÅØÔºå‰∏¶Âä†ÂÖ•Ëß£ÊûêÂæåÁöÑÂõûÈ•ãË®äÊÅØ
          setMessages(prevMessages => [
              ...prevMessages.filter(msg => msg.id !== feedbackRequestMessage.id),
              feedbackMessage
          ]);


      } catch (err) {
          console.error("Error getting or parsing feedback from Ollama:", err);
          const errorMsg = err.message || "ÁÑ°Ê≥ïÁç≤ÂèñÊàñËôïÁêÜ AI ÂõûÈ•ã„ÄÇ";
          setError(errorMsg);
          const errorFeedbackMessage = {
            id: Date.now() + 1,
            sender: 'ai',
            text: `ü§ñ Êä±Ê≠âÔºåÁç≤ÂèñÂõûÈ•ãÊôÇÈÅáÂà∞ÂïèÈ°åÔºö${errorMsg}`
          };
           setMessages(prevMessages => [
                ...prevMessages.filter(msg => msg.id !== feedbackRequestMessage.id),
                errorFeedbackMessage
           ]);
      } finally {
          setIsGettingFeedback(false);
      }
  };


  return (
    <Container fluid className="training-room-container vh-100">
      {/* --- ÁõÆÊ®ôËº∏ÂÖ•ËàáËßíËâ≤Ë≥áË®ä --- */}
      <Row className="goal-input-row bg-light p-2 mb-2 sticky-top shadow-sm">
        <Col md={7}>
          <InputGroup>
            <InputGroup.Text>üéØ</InputGroup.Text>
            <Form.Control
              type="text"
              placeholder={selectedCharacter ? "Ëº∏ÂÖ•Êú¨Ê¨°Á§æ‰∫§Ë®ìÁ∑¥ÁõÆÊ®ô..." : "Ë´ãÂÖàÈÅ∏ÊìáËßíËâ≤..."}
              value={goal}
              onChange={(e) => setGoal(e.target.value)}
              disabled={!selectedCharacter || isLoading || isGettingFeedback}
              aria-label="ËÅäÂ§©ÁõÆÊ®ô"
            />
          </InputGroup>
        </Col>
        <Col md={5} className="d-flex align-items-center justify-content-md-end mt-2 mt-md-0">
          {selectedCharacter ? (
            <div className="d-flex align-items-center">
                <span className="text-muted me-3 text-truncate" title={`Ëàá ${selectedCharacter.name} Ë®ìÁ∑¥‰∏≠`}>
                    Ëàá <strong>{selectedCharacter.name}</strong> Ë®ìÁ∑¥‰∏≠
                 </span>
                <Button
                    variant="outline-secondary"
                    size="sm"
                    onClick={handleClearChat}
                    disabled={messages.length === 0 || isLoading || isGettingFeedback}
                    title={`Ê∏ÖÈô§ÈóúÊñºÁõÆÊ®ô "${goal}" ÁöÑËÅäÂ§©Ë®òÈåÑ`}
                >
                    <i className="bi bi-trash3 me-1"></i> Ê∏ÖÈô§Ë®òÈåÑ
                </Button>
            </div>

          ) : (
            <Alert variant="warning" className="p-1 mb-0 small w-100 text-center">
              Ë´ãÂÖàËá≥ <a href="#" onClick={(e) => { e.preventDefault(); alert('Ë´ãÈªûÊìä‰∏äÊñπÂ∞éË¶ΩÂàóÁöÑ„ÄåËßíËâ≤È§®„Äç'); }}>ËßíËâ≤È§®</a> ÈÅ∏ÊìáÊàñÊñ∞Â¢ûËßíËâ≤
            </Alert>
          )}
        </Col>
        {error && (
            <Col xs={12} className="mt-2">
                <Alert variant="danger" onClose={() => setError(null)} dismissible className="py-1 px-2 small mb-0">
                    {error}
                </Alert>
            </Col>
        )}
      </Row>

      {/* --- ËÅäÂ§©È°ØÁ§∫ÂçÄÂüü --- */}
      <Row className="chat-display-row">
        <Col>
          <ChatDisplay messages={messages} isLoading={isLoading /* Loading anaimation only for AI response, not feedback */} />
        </Col>
      </Row>

      {/* --- Ë®äÊÅØËº∏ÂÖ•ÂçÄÂüü --- */}
      <Row className="message-input-row sticky-bottom bg-light p-3">
        <Col>
          <MessageInput
            currentMessage={currentMessage}
            onMessageChange={setCurrentMessage}
            onSendMessage={handleSendMessage}
            onGetFeedback={handleGetFeedback}
            isLoading={isLoading}
            isGettingFeedback={isGettingFeedback} // ÂÇ≥ÈÅûÂõûÈ•ãËÆÄÂèñÁãÄÊÖã
            disabled={!selectedCharacter || !goal.trim()}
          />
        </Col>
      </Row>
    </Container>
  );
}

export default TrainingRoom;
