import React, { useState, useEffect, useRef, useCallback } from 'react';
import { Form, InputGroup, Container, Row, Col, Spinner, Alert, Button } from 'react-bootstrap';
import ChatDisplay from './ChatDisplay';
import MessageInput from './MessageInput';
import { sendMessageToOllama, getFeedbackFromOllama } from '../../services/ollamaService'; // ÂºïÂÖ• API ÊúçÂãô
import '../../styles/TrainingRoom.css'; // ÂºïÂÖ•ËÅäÂ§©ÂÆ§Ê®£Âºè

// Êú¨Âú∞ÂÑ≤Â≠òËÅäÂ§©Ë®òÈåÑÁöÑ Key ÂâçÁ∂¥
const CHAT_HISTORY_PREFIX = 'wingchat_history_';

function TrainingRoom({ selectedCharacter }) {
  const [goal, setGoal] = useState('');
  const [messages, setMessages] = useState([]);
  const [currentMessage, setCurrentMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [isGettingFeedback, setIsGettingFeedback] = useState(false); // Êñ∞Â¢ûËÆÄÂèñÂõûÈ•ãÁöÑÁãÄÊÖã
  const [error, setError] = useState(null);
  const currentGoalRef = useRef(goal); // ‰ΩøÁî® ref ËøΩËπ§Áï∂Ââç goalÔºåÁî®ÊñºÁï∞Ê≠•Êìç‰Ωú

  // Áî¢ÁîüÁî®ÊñºÂÑ≤Â≠òÁöÑ key
  const getStorageKey = useCallback(() => {
      if (!selectedCharacter || !goal.trim()) return null;
      // Áî®ËßíËâ≤ ID Âíå Goal ÂÅö Key (Goal ÂèØËÉΩÈÅéÈï∑ÊàñÂê´ÁâπÊÆäÂ≠óÁ¨¶ÔºåÁ∞°ÂñÆËôïÁêÜ)
      const goalKey = goal.trim().substring(0, 50).replace(/[^a-zA-Z0-9]/g, '_');
      return `${CHAT_HISTORY_PREFIX}${selectedCharacter.id}_${goalKey}`;
  }, [selectedCharacter, goal]);

  // Âä†ËºâËÅäÂ§©Ë®òÈåÑ
  useEffect(() => {
    currentGoalRef.current = goal; // ÂêåÊ≠• ref
    const storageKey = getStorageKey();
    if (storageKey) {
      const savedHistory = localStorage.getItem(storageKey);
      if (savedHistory) {
        try {
          setMessages(JSON.parse(savedHistory));
        } catch (e) {
          console.error("Failed to parse chat history", e);
          setMessages([]);
        }
      } else {
        setMessages([]); // Â¶ÇÊûúÊ≤íÊúâÂÑ≤Â≠òÁöÑÁ¥ÄÈåÑÔºåÊ∏ÖÁ©∫
      }
    } else if (selectedCharacter) {
        // ÊúâËßíËâ≤‰ΩÜÊ≤íÁõÆÊ®ôÊôÇÔºå‰πüÊ∏ÖÁ©∫Ë®äÊÅØ
        setMessages([]);
    }
     setError(null); // ÁõÆÊ®ôÊîπËÆäÊôÇÊ∏ÖÈô§ÈåØË™§
  }, [selectedCharacter, goal, getStorageKey]); // Áï∂ËßíËâ≤ÊàñÁõÆÊ®ôÊîπËÆäÊôÇËß∏Áôº

   // ‰øùÂ≠òËÅäÂ§©Ë®òÈåÑ
   useEffect(() => {
     const storageKey = getStorageKey();
     if (storageKey && messages.length > 0) { // Âè™Âú®ÊúâË®äÊÅØÊôÇ‰øùÂ≠ò
       localStorage.setItem(storageKey, JSON.stringify(messages));
     }
