  }

  // ÁôºÈÄÅË®äÊÅØËôïÁêÜ
  const handleSendMessage = async () => {
    if (!currentMessage.trim() || !selectedCharacter) return;
    if (!goal.trim()) {
      setError("Ë´ãÂÖàË®≠ÂÆöÊú¨Ê¨°ËÅäÂ§©ÁöÑÁõÆÊ®ôÔºÅ");
      return;
    }
    if (isLoading || isGettingFeedback) return; // Èò≤Ê≠¢ÈáçË§áÁôºÈÄÅ

    const newUserMessage = {
      id: Date.now(),
      sender: 'user',
      text: currentMessage,
    };

    // ÂÖàÊõ¥Êñ∞ UI
    setMessages(prevMessages => [...prevMessages, newUserMessage]);
    const currentInput = currentMessage; // ‰øùÂ≠òÁï∂ÂâçËº∏ÂÖ•ÔºåÂõ†ÁÇ∫ state Êõ¥Êñ∞ÂèØËÉΩÊòØÁï∞Ê≠•ÁöÑ
    setCurrentMessage('');
    setIsLoading(true);
    setError(null);

    try {
      // Ê∫ñÂÇôÁµ¶ Ollama ÁöÑÊ≠∑Âè≤Ë®òÈåÑ
      const historyForOllama = messages.map(msg => ({
        role: msg.sender === 'user' ? 'user' : 'assistant',
        content: msg.text
      }));
      // Âä†ÂÖ•ÊúÄÊñ∞ÁöÑ‰ΩøÁî®ËÄÖË®äÊÅØ
      historyForOllama.push({ role: 'user', content: currentInput }); // ‰ΩøÁî®‰øùÂ≠òÁöÑËº∏ÂÖ•

      const aiResponseText = await sendMessageToOllama(
        goal, // ‰ΩøÁî® state ‰∏≠ÁöÑ goal
        historyForOllama,
        selectedCharacter,
        "llama3" // ÊàñÂÖ∂‰ªñÊ®°Âûã
      );

      const newAiMessage = {
        id: Date.now() + 1,
        sender: 'ai',
        text: aiResponseText,
      };
      setMessages(prevMessages => [...prevMessages, newAiMessage]);

    } catch (err) {
      console.error("Error contacting Ollama:", err);
      const errorMsg = err.message || "ÁÑ°Ê≥ïÈÄ£Êé•Âà∞ AI Ê®°ÂûãÔºåË´ãÁ¢∫Ë™ç Ollama ÊúçÂãôÊ≠£Âú®ÈÅãË°å‰∏îÊ®°ÂûãÂ∑≤‰∏ãËºâ„ÄÇ";
      setError(errorMsg);
      // ÂèØ‰ª•ÈÅ∏ÊìáÊòØÂê¶Âú®ËÅäÂ§©‰∏≠È°ØÁ§∫ÈåØË™§Ë®äÊÅØ
      const errorAiMessage = {
        id: Date.now() + 1,
        sender: 'ai',
        text: `ü§ñ Êä±Ê≠âÔºåËôïÁêÜÊôÇÈÅáÂà∞ÂïèÈ°åÔºö${errorMsg}`,
      };
      setMessages(prevMessages => [...prevMessages, errorAiMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  // Áç≤ÂèñÂõûÈ•ãËôïÁêÜ
  const handleGetFeedback = async () => {
      if (!selectedCharacter || !goal.trim() || messages.length === 0) {
          alert("Ë´ãÂÖàË®≠ÂÆöÁõÆÊ®ô„ÄÅÈÅ∏ÊìáËßíËâ≤‰∏¶ÈÄ≤Ë°åËá≥Â∞ë‰∏ÄËº™Â∞çË©±ÔºåÊâçËÉΩË´ãÊ±ÇÂõûÈ•ã„ÄÇ");
          return;
      }
      if (isLoading || isGettingFeedback) return; // Èò≤Ê≠¢ÈáçË§áË´ãÊ±Ç

      setIsGettingFeedback(true);
      setError(null);
      // Âú®ËÅäÂ§©‰ªãÈù¢È°ØÁ§∫ÊèêÁ§∫
      const feedbackRequestMessage = {
          id: Date.now(),
          sender: 'system', // Áî® 'system' ÂçÄÂàÜÊôÆÈÄöÊ∂àÊÅØ
          text: '‚è≥ Ê≠£Âú®Âêë AI Ë´ãÊ±ÇÂ∞çË©±ÂõûÈ•ã...'
      };
      setMessages(prevMessages => [...prevMessages, feedbackRequestMessage]);


      try {
          // Ê∫ñÂÇôÁµ¶ Ollama ÁöÑÊ≠∑Âè≤Ë®òÈåÑ (‰∏çÂåÖÂê´Á≥ªÁµ±Ë´ãÊ±ÇÂõûÈ•ãÁöÑÊ∂àÊÅØ)
          const historyForFeedback = messages
            .filter(msg => msg.sender !== 'system') // ÈÅéÊøæÊéâÁ≥ªÁµ±Ê∂àÊÅØ
            .map(msg => ({
                role: msg.sender === 'user' ? 'user' : 'assistant',
                content: msg.text
            }));

          const feedbackText = await getFeedbackFromOllama(
              goal, // ‰ΩøÁî® ref ‰∏≠ÁöÑ goalÔºåÁ¢∫‰øùÊòØË´ãÊ±ÇÁï∂‰∏ãÁöÑ goal
              historyForFeedback,
              selectedCharacter,
              "llama3" // ÂèØ‰ª•Áî®Âêå‰∏ÄÂÄãÊàñ‰∏çÂêåÁöÑÊ®°Âûã
          );

          const feedbackMessage = {
              id: Date.now() + 1,
              sender: 'ai', // AI ÂõûÈ•ã‰πüÁî® ai sender
              // ÂèØ‰ª•Âú®ÂâçÈù¢Âä†‰∏äÊ®ôÁ§∫
              text: `üí° **Êú¨Ê¨°Â∞çË©±ÂõûÈ•ã (${goal})**\n\n${feedbackText}`
          };
          // ÁßªÈô§"Ê≠£Âú®Ë´ãÊ±Ç"ÁöÑË®äÊÅØÔºå‰∏¶Âä†ÂÖ•ÂõûÈ•ãË®äÊÅØ
          setMessages(prevMessages => [
              ...prevMessages.filter(msg => msg.id !== feedbackRequestMessage.id),
              feedbackMessage
          ]);

          // TODO: Â∞áÂõûÈ•ãÊï∏ÊìöÂÑ≤Â≠òËµ∑‰æÜÔºå‰æõ FeedbackWall ‰ΩøÁî®
          // ‰æãÂ¶Ç: saveFeedbackToStorage(goal, selectedCharacter.id, feedbackText, scores);

      } catch (err) {
          console.error("Error getting feedback from Ollama:", err);
          const errorMsg = err.message || "ÁÑ°Ê≥ïÁç≤Âèñ AI ÂõûÈ•ã„ÄÇ";
          setError(errorMsg);
           // ÁßªÈô§"Ê≠£Âú®Ë´ãÊ±Ç"ÁöÑË®äÊÅØÔºå‰∏¶Âä†ÂÖ•ÈåØË™§Ë®äÊÅØ
          const errorFeedbackMessage = {
            id: Date.now() + 1,
            sender: 'ai',
            text: `ü§ñ Êä±Ê≠âÔºåÁç≤ÂèñÂõûÈ•ãÊôÇÈÅáÂà∞ÂïèÈ°åÔºö${errorMsg}`
          };
           setMessages(prevMessages => [
                ...prevMessages.filter(msg => msg.id !== feedbackRequestMessage.id),
                errorFeedbackMessage
           ]);
      } finally {
          setIsGettingFeedback(false);
      }
  };


  return (
    <Container fluid className="training-room-container vh-100"> {/* vh-100 ËÆìÂÆÉ‰ΩîÊªøË¶ñÁ™óÈ´òÂ∫¶ */}
      {/* --- ÁõÆÊ®ôËº∏ÂÖ•ËàáËßíËâ≤Ë≥áË®ä --- */}
      <Row className="goal-input-row bg-light p-2 mb-2 sticky-top shadow-sm">
        <Col md={7}>
          <InputGroup>
            <InputGroup.Text>üéØ</InputGroup.Text>
            <Form.Control
              type="text"
              placeholder={selectedCharacter ? "Ëº∏ÂÖ•Êú¨Ê¨°Á§æ‰∫§Ë®ìÁ∑¥ÁõÆÊ®ô..." : "Ë´ãÂÖàÈÅ∏ÊìáËßíËâ≤..."}
              value={goal}
              onChange={(e) => setGoal(e.target.value)}
              disabled={!selectedCharacter || isLoading || isGettingFeedback}
              aria-label="ËÅäÂ§©ÁõÆÊ®ô"
            />
          </InputGroup>
        </Col>
        <Col md={5} className="d-flex align-items-center justify-content-md-end mt-2 mt-md-0">
          {selectedCharacter ? (
            <div className="d-flex align-items-center">
                <span className="text-muted me-3">
                    Ëàá <strong>{selectedCharacter.name}</strong> Ë®ìÁ∑¥‰∏≠
                 </span>
                <Button
                    variant="outline-secondary"
                    size="sm"
                    onClick={handleClearChat}
                    disabled={messages.length === 0 || isLoading || isGettingFeedback}
                    title={`Ê∏ÖÈô§ÈóúÊñºÁõÆÊ®ô "${goal}" ÁöÑËÅäÂ§©Ë®òÈåÑ`}
                >
                    <i className="bi bi-trash3 me-1"></i> Ê∏ÖÈô§Ë®òÈåÑ
                </Button>
            </div>

          ) : (
            <Alert variant="warning" className="p-1 mb-0 small w-100 text-center">
              Ë´ãÂÖàËá≥ <a href="#" onClick={(e) => { e.preventDefault(); /* ÂèØËÉΩÈúÄË¶Å App.js ÂÇ≥ÈÅû setActiveView */ alert('Ë´ãÈªûÊìä‰∏äÊñπÂ∞éË¶ΩÂàóÁöÑ„ÄåËßíËâ≤È§®„Äç'); }}>ËßíËâ≤È§®</a> ÈÅ∏ÊìáÊàñÊñ∞Â¢ûËßíËâ≤
            </Alert>
          )}
        </Col>
        {error && (
            <Col xs={12} className="mt-2">
                <Alert variant="danger" onClose={() => setError(null)} dismissible className="py-1 small">
                    {error}
                </Alert>
            </Col>
        )}
      </Row>

      {/* --- ËÅäÂ§©È°ØÁ§∫ÂçÄÂüü --- */}
      <Row className="chat-display-row">
        <Col>
          <ChatDisplay messages={messages} isLoading={isLoading || isGettingFeedback} />
        </Col>
      </Row>

      {/* --- Ë®äÊÅØËº∏ÂÖ•ÂçÄÂüü --- */}
      <Row className="message-input-row sticky-bottom bg-light p-3">
        <Col>
          <MessageInput
            currentMessage={currentMessage}
            onMessageChange={setCurrentMessage}
            onSendMessage={handleSendMessage}
            onGetFeedback={handleGetFeedback} // ÂÇ≥ÈÅûÂõûÈ•ãÂáΩÊï∏
            isLoading={isLoading}
            isGettingFeedback={isGettingFeedback} // ÂÇ≥ÈÅûÂõûÈ•ãËÆÄÂèñÁãÄÊÖã
            disabled={!selectedCharacter || !goal.trim()} // Ê≤íÊúâËßíËâ≤ÊàñÁõÆÊ®ôÊôÇÁ¶ÅÁî®Ëº∏ÂÖ•
          />
        </Col>
      </Row>
    </Container>
  );
}

export default TrainingRoom;
